import React, { useState } from 'react';
import Navbar from '../components/header/Navbar';
import CreateMenuItemForm from './CreateMenuItemForm';
import UpdateMenuItemForm from './UpdateMenuItemForm';

function AdminProfile() {
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [showUpdateForm, setShowUpdateForm] = useState(false);
  const [selectedMenuItemId, setSelectedMenuItemId] = useState(null);

  const handleShowCreateForm = () => {
    setShowCreateForm(true);
    setShowUpdateForm(false);
  };

  const handleShowUpdateForm = (menuItemId) => {
    setShowCreateForm(false);
    setShowUpdateForm(true);
    setSelectedMenuItemId(menuItemId);
  };

  return (
    <>
      <Navbar />
      <div className="admin-profile">      
        <h1 className="admin-profile__heading">Admin Profile</h1>
        <div className="admin-profile__add-item">
          <button onClick={handleShowCreateForm}>Add New Menu Item</button>
        </div>
        <div className="admin-profile__menu-items">
          <h2>Menu Items</h2>
          <ul className="admin-profile__menu-list">
            {/* Map over menu items and display each one */}
            <li>
              <div>
                {/* Pass the item.id to handleShowUpdateForm */}
                <button onClick={() => handleShowUpdateForm(item.id)}>Update</button>
                <button onClick={() => handleDeleteMenuItem(item.id)}>Delete</button>
              </div>
            </li>
          </ul>
        </div>
      </div>
      {/* Conditionally render the create and update forms based on state */}
      {showCreateForm && <CreateMenuItemForm />}
      {showUpdateForm && <UpdateMenuItemForm menuItemId={selectedMenuItemId} />}
    </>
  );
}

export default AdminProfile;
