import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import { toast } from "react-hot-toast";
import Navbar from "../components/header/Navbar";
import { addMenuItem, deleteMenuItem, updateMenuItem } from "../api";
import Login from "./Login";

const Profile = () => {
  const [newMenuItemData, setNewMenuItemData] = useState({
    name: "",
    price: 0,
  });
  const [updateMenuItemData, setUpdateMenuItemData] = useState({
    id: "",
    name: "",
    price: 0,
  });
  const [deleteMenuItemId, setDeleteMenuItemId] = useState("");

  const { role } = useSelector((state) => state.user);

  const handleAddMenuItem = async () => {
    try {
      const addedMenuItem = await addMenuItem(newMenuItemData);
      toast.success("Menu Item added successfully!");
    } catch (error) {
      toast.error("Error adding menu item");
    }
  };

  const handleUpdateMenuItem = async () => {
    try {
      const updatedMenuItem = await updateMenuItem(
        updateMenuItemData.id,
        updateMenuItemData
      );
      toast.success("Menu Item updated successfully!");
    } catch (error) {
      toast.error("Error updating menu item");
    }
  };

  const handleDeleteMenuItem = async () => {
    try {
      await deleteMenuItem(deleteMenuItemId);
      toast.success("Menu Item deleted successfully!");
    } catch (error) {
      toast.error("Error deleting menu item");
    }
  };
return (
    <>
      <Navbar />
      <Login/>
      <div className="profile">
        <h1>Welcome!</h1>
        {role === "Admin" && (
          <>
            <h2>Add New Menu Item</h2>
            <input
              type="text"
              placeholder="Name"
              value={newMenuItemData.name}
              onChange={(e) =>
                setNewMenuItemData({ ...newMenuItemData, name: e.target.value })
              }
            />
            <input
              type="number"
              placeholder="Price"
              value={newMenuItemData.price}
              onChange={(e) =>
                setNewMenuItemData({ ...newMenuItemData, price: e.target.value })
              }
            />
            <button onClick={handleAddMenuItem}>Add Menu Item</button>
            <h2>Update Menu Item by ID</h2>
            <input
              type="text"
              placeholder="ID"
              value={updateMenuItemData.id}
              onChange={(e) =>
                setUpdateMenuItemData({ ...updateMenuItemData, id: e.target.value })
              }
            />
            <input
              type="text"
              placeholder="New Name"
              value={updateMenuItemData.name}
              onChange={(e) =>
                setUpdateMenuItemData({ ...updateMenuItemData, name: e.target.value })
              }
            />
            <input
              type="number"
              placeholder="New Price"
              value={updateMenuItemData.price}
              onChange={(e) =>
                setUpdateMenuItemData({ ...updateMenuItemData, price: e.target.value })
              }
            /> 
<button onClick={handleUpdateMenuItem}>Update Menu Item</button>
            <h2>Delete Menu Item by ID</h2>
            <input
              type="text"
              placeholder="Enter Menu Item ID to delete"
              value={deleteMenuItemId}
              onChange={(e) => setDeleteMenuItemId(e.target.value)}
            />
            <button onClick={handleDeleteMenuItem}>Delete Menu Item</button>
          </>
        )}
      </div>
    </>
  );
};

export default Profile;
