import React, { useState } from 'react';
import Navbar from '../components/header/Navbar';
import CreateMenuItemForm from './createMenuItem';
import UpdateMenuItemForm from './UpdateMenuItem';
import { Link } from 'react-router-dom';

function AdminProfile() {
  const [updateMenuItemId, setUpdateMenuItemId] = useState('');

  const handleMenuItemIdChange = (e) => {
    setUpdateMenuItemId(e.target.value);
  };

  return (
    <>
      <Navbar />
      <div className="admin-profile">
        <h1 className="admin-profile__heading">Admin Profile</h1>
        <div className="admin-profile__add-item">
          {/* Link for updating a menu item */}
          <Link to={`/update-menu-item/${updateMenuItemId}`}>
            {/* Conditional rendering for clarity */}
            {updateMenuItemId && (
              <>
                Update Menu Item {updateMenuItemId}
                {/* Render UpdateMenuItemForm temporarily for data input */}
                <UpdateMenuItemForm menuItemId={updateMenuItemId} />
              </>
            )}
          </Link>

          {/* Link for creating a new menu item */}
          <Link to="/create-menu-item">Create Menu Item</Link>
        </div>
      </div>
    </>
  );
}

export default AdminProfile;
