import React, { useState, useEffect } from 'react';
import { fetchRestaurantData, addMenuItem, updateMenuItem, deleteMenuItem } from '../api'; 
import Navbar from '../components/header/Navbar';
import CreateMenuItemForm from './createMenuItem';
import UpdateMenuItemForm from './UpdateMenuItem';

function AdminProfile() {
  const [updateMenuItemId, setUpdateMenuItemId] = useState(null); // State to hold the ID of the menu item to 

  const handleAddMenuItem = async () => {
    try {
      const addedMenuItem = await addMenuItem(newMenuItemData);
      setMenuItems([...menuItems, addedMenuItem]);
      // Clear input fields after successful addition
      setNewMenuItemData({
        name: '',
        price: '',
      });
    } catch (error) {
      console.error('Error adding menu item:', error);
    }
  };

  const handleUpdateMenuItem = async (menuItemId, updatedMenuItemData) => {
    try {
      const updatedMenuItem = await updateMenuItem(menuItemId, updatedMenuItemData);
      setMenuItems(menuItems.map(item => (item.id === menuItemId ? updatedMenuItem : item)));
      setUpdateMenuItemId(null); // Reset the updateMenuItemId state after updating
    } catch (error) {
      console.error('Error updating menu item:', error);
    }
  };

  const handleDeleteMenuItem = async (menuItemId) => {
    try {
      await deleteMenuItem(menuItemId);
      setMenuItems(menuItems.filter(item => item.id !== menuItemId));
    } catch (error) {
      console.error('Error deleting menu item:', error);
    }
  };

  return (
    <>
      <Navbar />
      <div className="admin-profile">      
        <h1 className="admin-profile__heading">Admin Profile</h1>
        <div className="admin-profile__add-item">
          {/* Conditional rendering of CreateMenuItemForm */}
          <CreateMenuItemForm />
          {/* Conditional rendering of UpdateMenuItemForm */}
          {updateMenuItemId && (
            <UpdateMenuItemForm 
              menuItemId={updateMenuItemId} 
              handleUpdateMenuItem={handleUpdateMenuItem} 
              setUpdateMenuItemId={setUpdateMenuItemId} 
            />
          )}
        </div>
      </div>
    </>
  );
}

export default AdminProfile;
