import React, { useState } from 'react';
import Navbar from '../components/header/Navbar';
import CreateMenuItemForm from './CreateMenuItemForm';
import UpdateMenuItemForm from './UpdateMenuItem';

function AdminProfile() {
  // State to hold the ID of the menu item to be updated
  const [updateMenuItemId, setUpdateMenuItemId] = useState(null);

  // Function to handle the input change for menu item ID
  const handleMenuItemIdChange = (e) => {
    setUpdateMenuItemId(e.target.value);
  };

  return (
    <>
      <Navbar />
      <div className="admin-profile">      
        <h1 className="admin-profile__heading">Admin Profile</h1>
        <div className="admin-profile__add-item">
          {/* Link to show CreateMenuItemForm */}
          <a href="#" onClick={() => setUpdateMenuItemId(null)}>Create New Menu Item</a>
          {/* Link to show UpdateMenuItemForm */}
          <a href="#" onClick={() => setUpdateMenuItemId('update')}>Update Menu Item</a>
          {/* Render UpdateMenuItemForm with the menuItemId prop */}
          {updateMenuItemId === 'update' && <UpdateMenuItemForm />}
          <CreateMenuItemForm />
        </div>
      </div>
    </>
  );
}

export default AdminProfile;
