import React, { useState } from 'react';
import Navbar from '../components/header/Navbar';
import CreateMenuItemForm
 from './createMenuItem';
import { Link } from 'react-router-dom';

function AdminProfile() {
  // State to hold the ID of the menu item to be updated
  const [updateMenuItemId, setUpdateMenuItemId] = useState('');

  // Function to handle the input change for menu item ID
  const handleMenuItemIdChange = (e) => {
    setUpdateMenuItemId(e.target.value);
  };

  return (
    <>
      <Navbar />
      <div className="admin-profile">      
        <h1 className="admin-profile__heading">Admin Profile</h1>
        <div className="admin-profile__add-item">
          {/* Input field for entering menu item ID */}
          <input
            type="text"
            placeholder="Enter Menu Item ID"
            value={updateMenuItemId}
            onChange={handleMenuItemIdChange}
            className="admin-profile__input"
          />
          {/* Render UpdateMenuItemForm with the menuItemId prop */}
          {updateMenuItemId && (
            <Link to={`/updateMenuItem/${updateMenuItemId}`}>
              <button className="link-button">Update Menu Item</button>
            </Link>
          )}
          <Link to="/createMenuItem">
            <button className="link-button">Create Menu Item</button>
          </Link>
        </div>
      </div>
    </>
  );
}

export default AdminProfile;
