import { fetchRestaurantData } from "../api";
//import { fetchWishlistData } from "../api";
export const restaurant = await fetchRestaurantData();


async function fetchWishlistData() {
    try {
      const response = await fetch('https://localhost:7027/api/wishlist');
      const data = await response.json();
      return data; //
    } catch (error) {
      console.error('Error fetching restaurant data:', error);
      throw error; 
    }
  }
export const wishlistData = await fetchWishlistData();

async function retrieveWishlistDetails() {
    try {
      const wishlistData = await fetchWishlistData();
      const wishlistDetails = wishlistData.map(wishlistItem => ({
        userId: wishlistItem.userId,
        menuItemId: wishlistItem.menuItems[0].id // Assuming there's only one MenuItem per wishlist item
      }));
      return wishlistDetails;
    } catch (error) {
      console.error('Error retrieving wishlist details:', error);
      throw error; 
    }
  }
